def menu():
    music.playMelody("C D E F G A B C5 ", 120)
    option = game.ask_for_number("CELSIUS TO FAHRENHEIT",4)
    
    return option

def convertor_temperature(value : int, sprite_1 : Sprite, sprite_2 : Sprite):
    result = value * 9/5 + 32
    text = str(value) + " CELSIUS is equal to \n" + str(result) + " FAHRENHEIT"

    if (value < -273):
        sprite_2.set_position(80, 65)
        sprite_1.set_position(-100, -100)
        music.playSound(music.sounds(Sounds.POWER_DOWN))
        game.show_long_text("The value you introduced is lower than absolute 0", DialogLayout.BOTTOM)
    else:
        sprite_1.set_position(80, 40)
        sprite_2.set_position(-100, -100)
        music.playSound(music.sounds(Sounds.PowerUp))
        game.show_long_text(text, DialogLayout.BOTTOM)
        

    convertor_temperature(menu(), sprite_1, sprite_2)

